import Axios from "axios";
import React, { useState } from "react";
import MainHeaderLogin from "../header/MainHeaderLogin";
import { Button } from "react-bootstrap";
import { Redirect } from "react-router-dom";

function Register() {
  const [enteredUsername, setEnteredUsername] = useState("");
  const [enteredPassword, setEnteredPassword] = useState("");
  const [ok, setOk] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
    Axios({
      method: "POST",
      data: {
        username: enteredUsername,
        password: enteredPassword,
      },
      withCredentials: true,
      url: "http://localhost:3001/createUser",
    }).then((res) => {setOk(<div><Redirect to="/login" /></div>)});
  } //Sending the data to the backend for verification and encryption.


  function usernameHandler(event) {
    setEnteredUsername(event.target.value);
  }
  function passwordHandler(event) {
    setEnteredPassword(event.target.value);
  }//Handlers for data input
 
 
  return (
    <form id="regiter" onSubmit={handleSubmit}>
      <input
        type="text"
        onChange={usernameHandler}
        value={enteredUsername}
        placeholder="username"
      ></input>
      <input
        type="password"
        onChange={passwordHandler}
        value={enteredPassword}
        placeholder="password"
      ></input>
      <Button variant="primary" type="submit">
        submit
      </Button>
      <MainHeaderLogin></MainHeaderLogin>
      {ok}
    </form>

  );
}//Registration form

export default Register;
